import './App.css';
import Headers from './components/header/Header';
import Footer from './components/footer/Footer';
import FondoImagen from './components/fondoImagen/FondoImagen';
import CarriucelContenedor from './components/carrucelContenedor/CarriucelContenedor';
import NuevoVideo from './components/nuevoVideo/NuevoVideo';
import {} from ''
import { useState } from 'react';

function App() {

  const [cambio, setCambio] = useState(true);
  const [datos, setDatos] = useState([
   {
      "categoria": "FrontEnd",
      "titulo": "aprender reack",
      "video": "https://youtu.be/FEdqGdtZuwc",
      "imagen": "https://mooc.ut.ac.ir/lms/auth/joomdle/pluginfile_joomdle.php/2009/course/overviewfiles/front-end.png",
      "descripcion": "aprende a crear un calendario con javascrip html css",
    },
     {
      "categoria": "BackEnd",
      "titulo": "aprender Spring boot",
      "video": "https://youtu.be/FqztWwPIrKo",
      "imagen": "https://geekflare.com/wp-content/uploads/2021/09/Backend-solution.png",
      "descripcion": "aprende como crear una base de datos de backend con spring boot",
    }, {
      "categoria": "MY SQL",
      "titulo": "aprender MY SQL",
      "video": "https://youtu.be/8J0AoPZMVxA",
      "imagen": "https://www.tecnologias-informacion.com/sql.png",
      "descripcion": "todo sobre MYSQL base de datos mas usada",

    }, {
      "categoria": "Programacion",
      "imagen": "https://blog.educacionit.com/wp-content/uploads/2018/09/shutterstock-10338536170938-620x354-01.jpg",
      "titulo": "aprender Programacion",
      "video": "https://youtu.be/kqfaRah9eYY",
      "descripcion": "conociendo el mercado de la tecnologia",
    }

  ])
  const [equipo, setEquipo] = useState([
    {
      "codigoSeguridad": "098099",
      "color": "#28b463",
      "categoria": "FrontEnd",
      "descripcion": "Aprende a crear tu propia pagina web y aplicacion"
    }, {
      "codigoSeguridad": "098099",
      "color": "#21618c",
      "categoria": "Programacion",
      "descripcion": "Conoce mas sobre el amplio mundo de la programacion"
    }, {
      "codigoSeguridad": "098099",
      "color": "#C70039",
      "categoria": "MY SQL",
      "descripcion": "conociendo la base de datos mas usada MYSQL"
    }, {
      "codigoSeguridad": "098099",
      "color": "#d35400",
      "categoria": "BackEnd",
      "descripcion": "Aprende a crear tu propia API"
    }
  ])
  const [datosVideo,setDatosVideo]=useState([{
    "categoria": "Programacion",
    "titulo": "Inicios de Programacion",
    "video": "https://youtu.be/ra5ojn-Ax7U",
    "imagen": "https://blog.educacionit.com/wp-content/uploads/2018/09/shutterstock-10338536170938-620x354-01.jpg",
    "descripcion": "Primeros pasos para adentrarse al mundo de la programacion ",
    "color":"orange"
  }])

  const cambiarImagenVideo=(videoLista)=>{
    setDatosVideo([videoLista])
  }

  const datosNuevoEquipo = (listaNueva) => {
    setEquipo([...equipo, listaNueva])
  }


  const nuevoVideo = () => {
    setCambio(!cambio)
  }
  const registrarNuevo = (colaborador) => {
    setDatos([...datos, colaborador])
  }

  return (
    <div className="App">

      <Headers nuevoVideo={nuevoVideo} cambio={cambio} setcambio={setCambio} />
      {
        cambio === true ? <><FondoImagen datosVideo={datosVideo} />
          <CarriucelContenedor datos={datos} 
          equipo={equipo} 
          cambiarImagenVideo={cambiarImagenVideo} />
          </> : <NuevoVideo registrarNuevo={registrarNuevo}
           equipo={equipo} datosNuevoEquipo={datosNuevoEquipo} />
      }
      <Footer />
    </div>
  );
}

export default App;
