    props.info.length > 0 && <div className='carrucelInformacion'>
        <h2 style={{backgroundColor:props.datos.color}}>{props.datos.categoria}</h2>
        <p>{props.datos.descripcion}</p>
    </div>
  }</>
   

    
  )
}

