import React from 'react'
import './Informacion.css'
import ReactPlayer from 'react-player'

function Informacion(props) {
  

  return (<>
    {
      props.datosVideo.map((dat,index) => {
        let urld=dat.video;
        return <div className='informacion'>
          <div key={index} className='informacion_info'>
            <h2 style={{backgroundColor:dat.color}}>{dat.categoria}</h2>
            <h4>{dat.titulo}</h4>
            <p>{dat.descripcion}</p>
          </div>
          <ReactPlayer style={{border}}
          className="video"
          url={urld}
          controls     
          loop
        />
          {/* <img style={{border:`3px solid ${dat.color}`}} src={dat.imagen} /> */}

        </div>
      })
    }
  </>

  )
}

export default Informacion
