import React, { useState } from 'react'
import './NuevoVideo.css'
import Input from '../input/Input'
import Boton from '../boton/Boton'
import Tabla from '../tabla/Tabla'

function NuevoVideo() {
  const [state, setstate] = useState(true)

  const [titulo,setTitulo]=useState("")
  const [video,setVideo]=useState("")
  const [imagen,setImagen]=useState("")
  const [categoria,setCategoria]=useState("")
  const [descripcion,setDescripcion]=useState("")

  const [nombre,setNombre]=useState("")
  const [codigoSeguridad,setCodigoSeguridad]=useState("")
  const [color,setColor]=useState("#6B5BE2")

  const cambio=()=>{
    setstate(!state)
  }

  const nuevoDato={
    "titulo":titulo
  }

  return (
    <>{
      state === true ? <div className='contenedor-nuevoVideo'>
        <div className='nuevoVideo'>
          <h2>Nuevo Video</h2>
          <Input type="text" placeholder="Ingrese el Titulo" titulo={titulo} setTitulo={setTitulo}/>
          <Input type="text" placeholder="Ingrese el Link De Video" titulo={video} setTitulo={setVideo}/>
          <Input type="text" placeholder="Ingrese el Link de Imagen" titulo={imagen} setTitulo={setImagen}/>
          <Input type="text" placeholder="Escoja categoria" titulo={categoria} setTitulo={setCategoria}/>
          <Input type="text" placeholder="Descripcion" titulo={descripcion} setTitulo={setDescripcion}/>
         
          <div className='nuevo-video-botones'>
            <div className='nuevo-video-botones-1'>
              <Boton nombre={"Guardar"}/>
              <Boton nombre={"Limpiar"} />
            </div>
            <Boton nombre="Nueva Categoria" cambio={nuevaCategoria} />
          </div>
        </div>
      </div>
        : <div className='contenedor-nuevoVideo'>
          <div className='nuevoVideo'>
            <h2>Nuevo Video</h2>
            <Input type="text" placeholder="Ingrese su nombre" titulo={nombre} setTitulo={setNombre} />
            <Input type="text" placeholder="Ingrese una descripcion dela categoria" />
            <label>Color</label>
            <Input type="color" placeholder="color" titulo={color} setTitulo={setColor}/>            
            <Input type="text" placeholder="codigo de seguridad" titulo={codigoSeguridad} setTitulo={setCodigoSeguridad} />
            <div className='nuevo-video-botones'>
              <div className='nuevo-video-botones-1'>
                <Boton nombre={"Guardar"}/>
                <Boton nombre={"Limpiar"} />
              </div>

            </div>
            <Tabla />
          </div>
        </div>
    }</>
  )
}

export default NuevoVideo
