import React, { useState } from 'react'
import Input from '../input/Input'
import Boton from '../boton/Boton'
import Tabla from '../tabla/Tabla'
import './NuevoEquipo.css'

function NuevoEquipo(props) {

  const {equipo,datosNuevoEquipo}= props

  const [nombre, setNombre] = useState("")
  const [codigoSeguridad, setCodigoSeguridad] = useState("")
  const [color, setColor] = useState("#6B5BE2")
  const [descripcion, setDescripcion] = useState("")


  const guardar = (e) => {
    e.preventDefault()
    const dato = {
      "categoria": nombre,
      "codigoSeguridad": codigoSeguridad,
      "color": color,
      "descripcion": descripcion
    }
    datosNuevoEquipo(dato)
  }
  const limpiar = () => {
    setNombre("")
    setCodigoSeguridad("")
    setColor("#6B5BE2")
    setDescripcion("")
  }
  
  const editarEquipo={
    
  }

  return (
    <div className='contenedor-nuevoEquipo'>
      <div className='nuevoEquipo'>
        <h2>Nuevo Video</h2>
        <Input type="text" placeholder="Ingrese su nombre" titulo={nombre} setTitulo={setNombre} />
        <Input type="text" placeholder="Ingrese una descripcion dela categoria" titulo={descripcion} setTitulo={setDescripcion} />
        <label>Color</label>
        <Input type="color" placeholder="color" titulo={color} setTitulo={setColor} />
        <Input type="text" placeholder="codigo de seguridad" titulo={codigoSeguridad} setTitulo={setCodigoSeguridad} />
        <div className='nuevo-video-botones'>
          <div className='nuevo-video-botones-1'>
            <Boton type="submit" cambio={guardar} nombre={"Guardar"} />
            <Boton type={"button"} cambio={limpiar} nombre={"Limpiar"} />
          </div>

        </div>
        <Tabla equipo={equipo} />
      </div>
    </div>
  )
}

export default NuevoEquipo
