import './App.css';
import Headers from './components/header/Header';
import Footer from './components/footer/Footer';
import FondoImagen from './components/fondoImagen/FondoImagen';
import CarriucelContenedor from './components/carrucelContenedor/CarriucelContenedor';
import NuevoVideo from './components/nuevoVideo/NuevoVideo';
import { useState } from 'react';

function App() {

  const [cambio, setCambio] = useState(true);
  const[datos,setDatos]=useState([
    {"categoria":"Frontend",
      "titulo": "aprender reack",
      "video": "https://www.youtube.com/watch?v=30Skd85YjLc",
      "imagen": "https://mooc.ut.ac.ir/lms/auth/joomdle/pluginfile_joomdle.php/2009/course/overviewfiles/front-end.png",
      "descripcion": "aprende a usar lo mas basico de REACK",
    },{"categoria":"Backend",
      "titulo": "aprender reack",
      "video": "https://www.youtube.com/watch?v=30Skd85YjLc",
      "imagen": "https://geekflare.com/wp-content/uploads/2021/09/Backend-solution.png",
      "descripcion": "aprende como crear una base de datos de backend",
    },{"categoria":"mySQL",
      "titulo": "aprender reack",
      "video": "https://www.youtube.com/watch?v=30Skd85YjLc",
      "imagen": "https://www.tecnologias-informacion.com/sql.png",
      "descripcion": "todo sobre MYSQL",

    },{"categoria":"",
      "imagen":"https://blog.educacionit.com/wp-content/uploads/2018/09/shutterstock-10338536170938-620x354-01.jpg",
      "titulo": "aprender reack",
      "video": "https://www.youtube.com/watch?v=30Skd85YjLc",
      "descripcion": "primero pasos en el mundo de la programacion",
    }

])

const equipo=[
 {
  "codigoSeguridad": "098099",
  "color":"green",
  "categoria": "FrontEnd",
  "descripcion":"esta es la nueva opcion de FRONTEND"
 },{
  "codigoSeguridad": "098099",
  "color":"blue",
  "categoria": "Programacion",
  "descripcion":"esta es la nueva opcion de PROGRAMACION"
 },{
  "codigoSeguridad": "098099",
  "color":"red",
  "categoria": "MY SQL",
  "descripcion":"esta es la nueva opcion de MY SQL"
 },{
  "codigoSeguridad": "098099",
  "color":"brown",
  "categoria": "BackEnd",
  "descripcion":"esta es la nueva opcion de BACKEND"
 }
]


  const nuevoVideo = () => {
    setCambio(!cambio)
  }
  const registrarNuevo=(colaborador)=>{
    // console.log(colaborador)
    setDatos([...datos,colaborador])
  }

  return (
    <div className="App">
      <Headers nuevoVideo={nuevoVideo} cambio={cambio} setcambio={setCambio} />
      {
        cambio === true ? <><FondoImagen />
          <CarriucelContenedor datos={datos} equipo={equipo}/></> : <NuevoVideo registrarNuevo={registrarNuevo} />
      }


      <Footer />
    </div>
  );
}

export default App;
